function navigation(){const e=document.querySelector(".burger");e.addEventListener("click",()=>{let e=document.querySelector(".nav-links");return e.classList.toggle("show-on-mobile")})}function headerScroll(){window.addEventListener("scroll",()=>{let e=document.querySelector("header");return 43<window.scrollY?e.classList.add("add-bg"):e.classList.remove("add-bg")})}function progress(){let e=document.querySelector(".progress>ul"),t=document.querySelector(".col-right"),s=0;setInterval(()=>{s+=1,1==s?(e.classList.add("ready"),t.classList.add("ready")):2==s?(e.classList.replace("ready","started"),t.classList.replace("ready","started")):3==s?(e.classList.replace("started","event-one"),t.classList.replace("started","event-one")):4==s?(e.classList.replace("event-one","event-two"),t.classList.replace("event-one","event-two")):5==s&&(e.classList.replace("event-two","ended"),t.classList.replace("event-two","ended")),5<s&&(s=0,e.classList.replace("ended","ready"),t.classList.replace("ended","ready"))},5e3)}window.addEventListener("load",()=>{navigation(),headerScroll(),progress()});